import React from 'react';
import "@patternfly/react-core/dist/styles/base.css";
import "@patternfly/patternfly/patternfly.css"
import {BrowserRouter as Router, Routes, Route} from 'react-router-dom';


import {
  Button,
  Flex,
  FlexItem,
  Tabs,
  Tab,
  TabContent,
  TabTitleText,
  Split,
  SplitItem,
  BackgroundImage,
} from '@patternfly/react-core'

import Overview from './Overview';
import Homepage from './Homepage';


import GithubIcon from '@patternfly/react-icons/dist/esm/icons/github-icon'; 
import AnisbleIcon from '@patternfly/react-icons/dist/esm/icons/ansible-tower-icon'; 
import Background from './images/pfbg_1200.jpg';
import './App.css';



  



// get the current year for the footer
const getCurrYear = () => {
  var today = new Date(),
  currYear = today.getFullYear();
  return currYear;
}

function App() {
  return (
    <Router>
      <Routes>
        <Route exact path='/' exact element={<Homepage />}/>
        <Route path='/Student#' element={<Overview/>}/>
      </Routes>
    <span>
      <SeparateTabs />
      <footer className="footer">&#169; {getCurrYear()} - Red Hat</footer>
    </span>
    </Router>
  );
}

export default App;
